package com.xworkz.java.Collection;

import java.util.ArrayList;
import java.util.ListIterator;

public class ListIteratorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		ArrayList<String> fruits = new ArrayList<String>();
		fruits.add("Banana");
		fruits.add("Apple");
		fruits.add("Mango");
		fruits.add("Pomogranet");
		fruits.add("Orange");
		System.out.println(fruits);
		System.out.println("--------------------------");
		
       
		ListIterator<String> fruitList= fruits.listIterator();
		while(fruitList.hasNext()) {
			String fruit=fruitList.next();
			if(fruit.equals("Mango")) {
				fruitList.set("Strawberry");
				System.out.println(fruits);
			}
			if(fruit.equals("Orange")) {
				fruitList.add("Kiwi");
				System.out.println(fruits);
			}
			if(fruit.equals("Pomogranet")) {
				fruitList.remove();
				System.out.println(fruits);
			}
			
		}
	}

}


















































































































































































































































































































































































































